INSERT INTO `wp_comments` VALUES (1,1,'Hr WordPress','','http://wordpress.org/','','2013-04-21 17:30:30','2013-04-21 17:30:30','Hej, dette er en kommentar.\nFor at slette en kommentar logger du blot ind og læser indlæggets kommentarer. Der har du mulighed for at redigere eller slette dem.',0,'1','','',0,0);
